/*【用户面板】*/

/*动画*/
.userbar {
	overflow: visible !important;
	position: fixed !important;
	top: 20px !important;
	right: 0 !important;
	margin: 0 !important;
	padding: 0 !important;
	padding-right: 10px !important;
}

.userba * {
	backface-visibility: hidden;
}

.userbar>ul {
	overflow: visible;
	text-align: center;
	margin-top: 10px;
	margin-right: 20px;
	perspective: 800px;
}

.userbar>ul>li.u_username:hover~li:nth-of-type(2) {
	transition-delay: 0s;
}

.userbar>ul>li.u_username:hover~li:nth-of-type(4) {
	transition-delay: .05s;
}

.userbar>ul>li.u_username:hover~li:nth-of-type(6) {
	transition-delay: .1s;
}

.userbar>ul>li.u_username:hover~li {
	transition-delay: .15s;
}

.userbar>ul>li.u_setting:hover~li:nth-of-type(4) {
	transition-delay: 0s;
}

.userbar>ul>li.u_setting:hover~li:nth-of-type(6) {
	transition-delay: .05s;
}

.userbar>ul>li.u_setting:hover~li {
	transition-delay: .1s;
}

.userbar>ul>li.u_news:hover~li:nth-of-type(6) {
	transition-delay: 0s;
}

.userbar>ul>li.u_news:hover~li {
	transition-delay: .05s;
}

.userbar>ul>li {
	height: 100%;
	position: relative;
	margin: 0 !important;
	padding: 0 !important;
	margin-bottom: 10px !important;
	opacity: 1;
	transform-origin: 100% 50%;
	transition-property: transform, opacity;
	transition-duration: 1s;
	transition-timing-function: cubic-bezier(0.23, 1, 0.32, 1);
}

.userbar>ul>li,
.userbar>ul>li * {
	float: none !important;
}

.userbar>ul>li:hover~li {
	transform: translateX(50px) rotateY(-90deg) translateZ(-60px);
	opacity: 0;
}

.userbar>ul>li.u_wallet:hover~li,
.userbar>ul>li.u_tbmall:hover~li,
.userbar>ul>li.u_hermes:hover~li,
.userbar>ul>li.u_login:hover~li,
.userbar>ul>li.u_reg:hover~li {
	opacity: 1;
	transform: none;
}

.u_menu_item {
	display: block;
	margin: 0 auto !important;
	padding: 0 !important;
	position: relative !important;
	height: 50px !important;
	width: 50px !important;
	border: none !important;
	border-radius: 50%;
	background: transparent;
	box-shadow: 0 2px 4px 0 rgba(0,0,0,0.08), 0 2px 8px 0 rgba(0,0,0,0.08);
	transition-property: box-shadow, color, background;
	transition-duration: .5s;
	transition-timing-function: cubic-bezier(0.23, 1, 0.32, 1);
}

.u_menu_item.u_menu_hover,
.u_menu_item:hover {
	background: transparent;
	box-shadow: 0 2px 8px 0 rgba(0,0,0,0.1), 0 2px 16px 0 rgba(0,0,0,0.08);
}

.u_menu_wrap,
.u_menu_item>a {
	display: block;
	border-radius: 50%;
	height: 50px !important;
	width: 50px !important;
	background: var(--m-ecolor) !important;
	padding: 0 !important;
	margin: 0 !important;
	transition-property: background;
	transition-duration: 1s;
	transition-timing-function: cubic-bezier(0.23, 1, 0.32, 1);
	text-decoration: none !important;
}

.u_hermes>.u_menu_item>a {
	font-size: 14px;
	line-height: 18px;
	letter-spacing: 2px;
	text-indent: 2px;
	padding: 6px !important;
	padding-top: 7px !important;
	box-sizing: border-box;
}

.u_hermes a,
.u_login a,
.u_reg a {
	color: #fff !important;
	font-size: 14px !important;
	line-height: 50px;
	font-weight: bold;
}

body>.userbar>ul>.u_hermes>a,
body>.userbar>ul>.u_login>a,
body>.userbar>ul>.u_reg>a {
	box-shadow: 0 2px 4px 0 rgba(0,0,0,0.08), 0 2px 8px 0 rgba(0,0,0,0.08);
}

body>.userbar>ul>.u_login>a,
body>.userbar>ul>.u_reg>a {
	text-indent: 0;
	letter-spacing: 0;
	font-size: 16px;
	line-height: 34px;
	box-shadow: 0 2px 4px 0 rgba(0,0,0,0.08), 0 2px 8px 0 rgba(0,0,0,0.08);
}

.u_hermes a:hover,
.u_login a:hover,
.u_reg a:hover {
	background: var(--m-ehcolor) !important;
}

.u_menu_hover:after {
	display: none !important;
}

.u_news_wrap span {
	color: #fff !important;
	display: block;
	background: var(--m-ecolor) !important;
	line-height: 12px !important;
	position: absolute !important;
	padding: 2px 4px !important;
	text-align: center !important;
	top: auto !important;
	bottom: -6px !important;
	left: 50% !important;
	transform: translateX(-50%);
	font-size: 12px !important;
	border-radius: 6px !important;
	white-space: nowrap !important;
	box-shadow: 0 1px 2px 0 rgba(0,0,0,0.08), 0 2px 4px 0 rgba(0,0,0,0.08);
}

.u_ddl {
	position: absolute !important;
	display: block !important;
	top: 38px !important;
	left: 50% !important;
	transform: translateX(-50%);
	right: auto !important;
	overflow: visible !important;
	pointer-events: none;
	opacity: 0;
	transition-property: opacity;
	transition-duration: .5s;
	transition-timing-function: cubic-bezier(0.23, 1, 0.32, 1);
}

.u_menu_hover~.u_ddl,
li:hover>.u_ddl {
	pointer-events: auto;
	opacity: 1;
}

.u_ddl_con {
	position: relative !important;
	border: none !important;
	background: transparent !important;
	padding-bottom: 10px;
	overflow: visible !important;
}

.u_ddl_con_top {
	background: transparent !important;
}

.u_ddl_tit {
	background: transparent !important;
}

.u_ddl_con ul {
	display: block;
	overflow: visible;
	padding: 10px !important;
	padding-bottom: 0 !important;
}

.u_ddl_con ul.sys_notify_last {
	padding-top: 0 !important;
}

.u_ddl_con li {
	margin-top: 10px;
	padding: 0 !important;
	width: auto !important;
	transform: translateX(-50px) rotateY(90deg) translateZ(-60px);
	transform-style: preserve-3d;
	opacity: 0;
	transform-origin: 100% 50%;
	transition-property: transform, opacity;
	transition-duration: 1s;
	transition-timing-function: cubic-bezier(0.23, 1, 0.32, 1);
}

.u_ddl_con li:nth-of-type(1) {
	transition-delay: 0s;
}

.u_ddl_con li:nth-of-type(2) {
	transition-delay: .05s;
}

.u_ddl_con li:nth-of-type(3) {
	transition-delay: .1s;
}

.u_ddl_con li:nth-of-type(4) {
	transition-delay: .15s;
}

.u_ddl_con li:nth-of-type(5) {
	transition-delay: .2s;
}

.u_ddl_con li:nth-of-type(6) {
	transition-delay: .25s;
}

.u_ddl_con li:nth-of-type(7) {
	transition-delay: .3s;
}

.u_ddl_con li:nth-of-type(8) {
	transition-delay: .35s;
}

.u_ddl_con li:nth-of-type(9) {
	transition-delay: .4s;
}

.category_item_last {
	transition-delay: .3s !important;
}

.u_menu_hover~.u_ddl .u_ddl_con li,
li:hover>.u_ddl .u_ddl_con li {
