/* =========================== 清理视觉污染 =========================== */

	/* Optimize font. */
	body {
		font-family: Helvetica, Arial, Microsoft YaHei, sans-serif;
	}
	.info b {
		font-weight: normal;
	}
	.b-head .b-head-t h2 {
		font-size: 20px !important;
		color: #505050;
	}

	/* Video thumbnails opacity morph. */
	.topic-preview-wrapper,
	.rm-item,
	.pmt-link a,
	.v, .v-item,
	.lv-item,
	.b-r .b-body {
		opacity: .7;
	}
	.topic-preview-wrapper:hover,
	.rm-item:hover,
	.pmt-link a:hover,
	.v:hover,
	.v-item:hover,
	.lv-item:hover,
	.b-r .b-body:hover {
		opacity: 1;
		transition: .1s all linear;
	}

	/* Increase thumbnail radius. */
	.v-live-recommend .v-list-live li .lv-item,
	.v-list-live li .lv-preview img,
	.rm-item .preview,
	.v .preview,
	.lv-mask {
		border-radius: 15px !important;
	}

	/* ============================== 删删删 ============================== */

	/* Remove top-most banner. */
	.b-header-mask-bg {
		background: none !important;
	}
	.header {
		background: none !important;
	}
	.header .header-layer, .header .header-link,
	.header .h-center {
		display: none !important;
	}

	/* Remove navbar items. */
	.z_top_container {
		z-index: 100;
	}
	.z_top_container .hbili,
	.z_top_container .b-gc,
	.z_top_container .live,
	.z_top_container .b-zb,
	.z_top_container .b-ml {
		display: none !important;
	}

	/* Increase top margin space. */
	.z_top .z_top_nav ul {
	    margin-left: 5px;
	}
	.z_top_container {
		margin-top: 10px;
	}
	.i-link a {
		border-radius: 5px 5px 5px 5px;
		height: 43px;
	}

	/* Optimize numbar and search bar. */
	.header .num {
		margin-top: 50px;
		margin-bottom: 2em;
	}
	.header .num .menu_wrapper {
		z-index: 1000;
	}
	.header .num ul.nav-menu,
	.header .num .menu-r {
		z-index: -100000 !important;
	}
	.header .num .search {
		background-color: transparent;
		border-radius: 4px;
		border: 1px solid rgba(0, 0, 0, 0.12) !important;
		top: -48px;
		right: 365px;
		width: 470px;
	    z-index: 1001;
	}
	.header .num .search #searchform{
		border-radius: 0px;
	}
	.header .num .search .link-ranking {
		border-radius: 0px;
		border-right: 1px solid rgba(0, 0, 0, 0.12);
	}
	.header .num .search .search-keyword {
		width: 390px;
	}
	.z_top_container .z_top {
		box-shadow: none;
	}
	.header .num .menu-r .random-p {
		display: none;
	}
	.uns_box li.u-i.b-post a.i-link {
		background: #F25D8E;
		height: 43px;
	    border-radius: 5px 5px 5px 5px;
		margin-left: 1em;
	}

	/* Remove redundant autocomplete box. */
	.ui-autocomplete {
		display: none !important;
	}

	/* Remove unnecessary sections. */
	.container-top-wrapper,
	#b_live.container-row,
	#b_bangumi,
	#b_recommend {
		display: none;
	}

	/* Remove section header img. */
	.b-head .b-head-i {
		display: none !important;
	}
	.b-head .b-head-t {
		margin-left: 0px !important;
	}
	#b_live .b-head .b-head-t h2 {
		margin-top: 3px !important;
	}

	/* Remove promote header img. */
	.pmt-icon {
		display: none !important;
	}
	.pmt-inline {
		margin-left: 2em;
	}
	.pmt-link a {
		color: #848484 !important;
	}

	/* Remove banner ad. */
	[data-loc-id="2"],
	[data-loc-id="3"],
	#b_promote,
	.ad-f,
	.ad {
		display: none !important;
	}

